sys_module.content:
(function executeRule(current, previous /*null when async*/ ) {

    // Only generate donation ID if mode is specified and donation_id is empty
    if (!current.mode) {
        gs.addErrorMessage('No mode specified, skipping donation ID generation');
        return;
    }

    try {
        // Use current date/time for formatting since sys_created_on might not be set yet
        var gdt = new GlideDateTime();
        var formattedDateTime = gdt.getDate().getByFormat("yyyyMMdd") + gdt.getTime().getByFormat("HHmmss");

        // Get unique sequence number for the mode of donation from EkklesiumUtils left padded with zeros to make it 4 digits
        var ekklesiumUtils = new EkklesiumUtils();
        var uniqueNumber = ekklesiumUtils.getDonationSequenceNumber(current.mode);

        uniqueNumber = uniqueNumber.toString().padStart(4, '0');
        gs.info('Padded sequence number: ' + uniqueNumber);

        // Donation Number Format: "mode"+formattedDateTime+uniqueNumber
        current.donation_id = current.mode + formattedDateTime + uniqueNumber;

        gs.addInfoMessage('Generated donation ID: ' + current.donation_id);
    } catch (e) {
        gs.addErrorMessage('Error generating donation ID: ' + e.message);
    }

})(current, previous);
