sys_module.content:
/**
 * Business Rule: Update Donation Status on Slip Item Insert
 * When: after
 * Insert: true
 * Updates donation.donation_item_status to "captured" when slip item created
 */

(function executeRule(current, previous) {

    // Only process if active slip item
    if (!current.active_donation_slip_item) {
        gs.debug('UpdateDonationStatus: Slip item not active, skipping');
        return;
    }

    if (current.donation.nil()) {
        gs.warn('UpdateDonationStatus: No donation linked to slip item');
        return;
    }

    try {
        var donation = new GlideRecord('x_1644142_ekkles_0_donation');
        if (donation.get(current.donation)) {
            donation.donation_item_status = 'captured';
            donation.setWorkflow(false); // Prevent recursion
            donation.update();

            gs.info('UpdateDonationStatus: Set donation ' + donation.number + ' to captured');
        }
    } catch (e) {
        gs.error('UpdateDonationStatus: ' + e.message);
    }

})(current, previous);
