sys_module.content:
/**
 * Business Rule: Revert Donation Status on Slip Item Deactivate
 * Table: x_1644142_ekkles_0_donation_slip_item
 * When: after
 * Action: update
 * 
 * Reverts donation status when slip item deactivated:
 * - fully_refunded → received
 * - partially_refunded → received (if no other active items)
 * - captured → received (if no other active items)
 */

(function executeRule(current, previous) {

    // Only process when slip item becomes inactive
    if (current.active_donation_slip_item || !previous.active_donation_slip_item) {
        return;
    }

    if (current.donation.nil()) {
        return;
    }

    try {
        // Check if donation has any OTHER active slip items
        var otherActive = new GlideRecord('x_1644142_ekkles_0_donation_slip_item');
        otherActive.addQuery('donation', current.donation);
        otherActive.addQuery('active_donation_slip_item', true);
        otherActive.addQuery('sys_id', '!=', current.sys_id);
        otherActive.query();

        if (!otherActive.hasNext()) {
            // No other active items, revert donation status
            var donation = new GlideRecord('x_1644142_ekkles_0_donation');
            if (donation.get(current.donation)) {

                var currentStatus = donation.donation_item_status.toString();
                var netAmount = parseFloat(donation.net_amount) || 0;

                // Determine target status based on refund state
                var targetStatus = 'received';

                // If fully refunded, keep as fully_refunded
                if (netAmount <= 0) {
                    targetStatus = 'fully_refunded';
                }
                // If partially refunded, keep as partially_refunded  
                else if (parseFloat(donation.refund_amount) > 0) {
                    targetStatus = 'partially_refunded';
                }
                // Otherwise revert to received
                else {
                    targetStatus = 'received';
                }

                donation.donation_item_status = targetStatus;
                donation.setWorkflow(false);
                donation.update();

                gs.info('RevertDonationStatus: Reverted donation ' + donation.number + 
                       ' from ' + currentStatus + ' to ' + targetStatus);
            }
        } else {
            gs.debug('RevertDonationStatus: Donation ' + current.donation.getDisplayValue() + 
                    ' has other active slip items, not reverting status');
        }
    } catch (e) {
        gs.error('RevertDonationStatus: ' + e.message);
    }

})(current, previous);
