sys_module.content:
var EkklesiumUtils = Class.create();
EkklesiumUtils.prototype = {
    initialize: function() {},
    getDonationSequenceNumber: function(mode) {
        //Generate a unique sequence number based on the mode of donation
        //Pick the system propery  based on mode
		//x_1644142_ekkles_0.transfer.donation.sequence.number
        var propertyName = 'x_1644142_ekkles_0.' + mode + '.donation.sequence.number';
        var sequenceNumber = gs.getProperty(propertyName);
        //increment the sequence number for next use. Property is string type
        var nextSequenceNumber = parseInt(sequenceNumber) + 1;
        gs.setProperty(propertyName, nextSequenceNumber.toString());
		return sequenceNumber;
    },

    type: 'EkklesiumUtils'
};
