sys_ui_action.script:
/**
 * UI Action: Initiate Slip
 * Captures eligible donations for a draft slip and transitions status to 'initiated'
 * 
 * Table: x_1644142_ekkles_0_donation_slip
 * When: User clicks "Initiate Slip" button on a draft slip
 * Role: x_1644142_ekkles_0.secretariat
 */

(function() {

    try {
        // Call the script include to initiate the slip
        var slipUtils = new DonationSlipUtils();
        var result = slipUtils.initiateSlip(current.sys_id.toString());

        if (result.success) {
            if (result.itemsCreated > 0) {
                gs.addInfoMessage('Successfully initiated slip with ' + result.itemsCreated + ' donation(s)');
            } else {
                gs.addInfoMessage('Slip initiated successfully, but no eligible donations found for ' + 
                                  current.congregant.getDisplayValue() + ' in tax year ' + current.tax_year);
            }

            // Reload the form to show updated status and related items
            action.setRedirectURL(current);
        } else {
            // Display all errors from script include
            if (result.errors && result.errors.length > 0) {
                for (var i = 0; i < result.errors.length; i++) {
                    gs.addErrorMessage(result.errors[i]);
                }
            } else {
                gs.addErrorMessage('Failed to initiate slip. Please check the system logs for details.');
            }
        }

    } catch (e) {
        gs.addErrorMessage('An error occurred while initiating the slip: ' + e.message);
        gs.error('UIA_InitiateSlip: Exception caught - ' + e.message + '\nStack: ' + e.stack);
    }

})();
